skills.code:
db.skills.find({
    $and: {
        name: { $regex: /^mongos?db$/i },
        level: { $gt: 'basic', $lte: 'intermediate' },
        acquiredBy: {
            $in: [
                'studying in Telerik Academy',
                'working on my own projects',
                'preparing to present on the topic'
            ]
        }
    }
});
