skills.code:
db.my_skills.find({
    $and: [
        { name: { $regex: /^mongo\s+db$/i } },
        { level: { $gt: "basic", $lte: "intermediate" } },
        { acquiredBy: {
               $in: [
                    "MEAN stack course in Telerik Academy",
                 "working on my own web projects",
                   "using it as a part of NodeJS presentations I've led"
               ]
           }
        }
    ] 
});
